import React, { useEffect, useState } from 'react'
import { Box, Button, Grid, Typography, Card, FormControl, FormHelperText, InputLabel, Select, MenuItem, IconButton, CircularProgress, Stack, TextField } from '@mui/material';
import { ItemAnimation } from '../components/ItemAnimation'
import PirelliIcon from '../images/Logo_Pirelli.png'
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { StaticDateTimePicker } from '@mui/x-date-pickers/StaticDateTimePicker';
import dayjs from 'dayjs';


function MainPage() {
  const [startDate, setStartDate] = useState('2022-04-17T15:30')
  const [endDate, setEndDate] = useState('2022-04-17T15:30')

  function getMetrics(){
    console.log("start date=", startDate, " - end date=", endDate)
  }

  const handleDateChange = (date) => {
    console.log("date", date)
    setStartDate(date.$y+"-"+(date.$M+1)+"-"+date.$D)
  }

  return (
      <Box style={{ padding: '15px' }}>
              <Box>
                  <ItemAnimation variant='down' animateInView={false} delay={0} oneTime={true}>
                      <Stack sx={{ marginBottom: '10px' }} spacing={{ xs: 1, sm: 2, md: 3, lg: 4 }} direction='row' justifyContent="center" alignItems="center">
                          <img src={PirelliIcon} lt="Pirelli logo" style={{ width: '100px', padding: '0px' }} />
                          <Typography variant='h4'>Metrics Calulator</Typography>
                      </Stack>
                  </ItemAnimation>
                  <ItemAnimation variant='down' animateInView={false} delay={0.5} oneTime={true}>
                      <Card sx={{ boxShadow: 3 }}>
                          <Box sx={{ padding: '10px' }}>
                              <Grid container spacing={{ xs: 1, sm: 2, md: 3, lg: 4 }}>

                                <LocalizationProvider dateAdapter={AdapterDayjs}>
                                  <Grid item xs={12} sm={6}>
                                    <StaticDateTimePicker ampm={false}
                                      onChange={handleDateChange}/>
                                  </Grid>

                                  <Grid item xs={12} sm={6}>
                                    <StaticDateTimePicker ampm={false} onAccept={console.log("ciao")}
                                    onChange={(event) => {
                                      console.log("event", event)
                                      setEndDate(event.target.value)
                                      }}/>
                                  </Grid>
                                </LocalizationProvider>

                                  <Grid item xs={12}>
                                      <Button fullWidth onClick={() => getMetrics()} variant='outlined' >
                                          Create
                                      </Button>
                                  </Grid>

                              </Grid>
                          </Box>

                      </Card>
                  </ItemAnimation>
              </Box>

      </Box >
  )
  }
  
  export default MainPage;
